module ApplicationHelper

  def sort_link(column, title = nil)
    title ||= column.titleize
    direction = column == sort_column && sort_direction == "asc" ? "desc" : "asc"
    icon = sort_direction == "asc" ? 'chevron-circle-up' : 'chevron-circle-down'
    icon = column == sort_column ? icon : "minus-circle"
    link_to "#{fa_icon(icon, text: title)}".html_safe, {column: column, direction: direction}
  end

  def forum_area?
    controllers = %w[forums forum_categories forum_threads forum_topics forum_posts]
    controllers.include?(controller_name)
  end

  def root_controller?
    controller_name == 'home'
  end

  def admin?
    current_user.has_role?(:admin)
  end


  def unconfirmed_user_count
    User.select{ |user| user.confirmed_at.nil? }.count
  end

  def admin_or_owner?(post)
    current_user.has_role?(:admin) || post.user == current_user
  end

  def user_admin?
    user_signed_in? && current_user.has_role?(:admin)
  end

  def avatar_url(user)
    default_url = asset_path('guest.png')
    gravatar_id = Digest::MD5.hexdigest(user.email.downcase)
    "http://gravatar.com/avatar/#{gravatar_id}.png?s=48&d=#{CGI.escape(default_url)}"
  end

  def peace_icon
    '<img src="data:image/svg+xml;utf8;base64," />'
  end

  def peace_icon_white
    '<img src="data:image/svg+xml;utf8;base64," />'
  end
end
